import React, { Component } from "react";

import Navigation from "../Navigation";
import HamburgerMenu from "../HamburgerMenu";

class Menu extends Component {
  state = {
    hamburgerIsOpen: false,
    menuClassName: 'menu'
  };

  handleHamburgerMenu = () => {
    this.setState({
      hamburgerIsOpen: !this.state.hamburgerIsOpen,
    });
    if(this.state.hamburgerIsOpen){
        this.setState({
            menuClassName: 'menu active'
        });
        console.log("open")
        console.log(this.state.menuClassName)
    }else{
        this.setState({
            menuClassName:'menu'
        })
        console.log("closed")
        console.log(this.state.menuClassName)
    }
  };

  handleHamburgerMenuClose = () => {
    this.setState({
      hamburgerIsOpen: false,
    });

  };

  render() {
    return (
      <>
        <Navigation
          handleHamburgerMenu={this.handleHamburgerMenu}
          handleHamburgerMenuClose={this.handleHamburgerMenuClose}
        />
        <HamburgerMenu
          handleHamburgerMenu={this.handleHamburgerMenu}
          handleHamburgerMenuClose={this.handleHamburgerMenuClose}
        />
      </>
    );
  }
}

export default Menu;
