import React, { Component } from "react";
// import className from 'classnames';

import { Link } from "react-scroll";
import "./menu.scss";

var classNames = require('classnames');

class Navigation extends Component{
    constructor(props){
        super(props);
    }

    render(){
        let menuClass = classNames({
            'menu active': this.state.hamburgerIsOpen,
            'menu': !this.state.hamburgerIsOpen
        });

        return (
            <nav className={menuClass}>
              <ul>
                <li>
                  <a href="/" title="home" smooth={"true"} offset={0} duration={500}>
                    home
                  </a>
                </li>
                <li>
                  <Link
                    to="/"
                    title="about-me"
                    smooth={"true"}
                    offset={0}
                    duration={500}
                  >
                    about me
                  </Link>
                </li>
                <li>
                  <Link
                    to="/"
                    title="my-works"
                    smooth={"true"}
                    offset={0}
                    duration={800}
                  >
                    my works
                  </Link>
                </li>
                <li>
                  <Link
                    to="/"
                    title="contact"
                    smooth={"true"}
                    offset={0}
                    duration={1000}
                  >
                    contact
                  </Link>
                </li>
              </ul>
            </nav>
          );
    }
}
